import { Container, Row, Col, Tab, Nav } from 'react-bootstrap';
import { ProjectCard } from './ProjectCard';
import colorSharp from '../assets/img/color-sharp.png';
import 'animate.css';
import data from '../aboutDescriptions.json';
import TrackVisibility from 'react-on-screen';

export const About2 = () => {
  return (
    <section className="project" id="project">
      <Container>
        <Row>
          <Col size={12}>
            <TrackVisibility>
              {({ isVisible }) => (
                <div
                  className={
                    isVisible ? 'animate__animated animate__fadeIn' : ''
                  }
                >
                  <h2>About Me</h2>
                  <p>Hover over or tap on card for more information.</p>
                  <Tab.Container id="projects-tabs" defaultActiveKey="first">
                    <Nav
                      variant="pills"
                      className="nav-pills mb-5 flex-sm-row"
                      id="pills-tab"
                    >
                      <Nav.Item>
                        <Nav.Link eventKey="first">Storytelling</Nav.Link>
                      </Nav.Item>
                      <Nav.Item>
                        <Nav.Link eventKey="second">Mediation</Nav.Link>
                      </Nav.Item>
                      <Nav.Item>
                        <Nav.Link eventKey="third">Journalism</Nav.Link>
                      </Nav.Item>
                      <Nav.Item>
                        <Nav.Link eventKey="fourth">
                          Web Development & Design
                        </Nav.Link>
                      </Nav.Item>
                    </Nav>
                    {data.map((project, index) => (
                      <Tab.Content
                        id="slideInUp"
                        className={
                          isVisible
                            ? 'animate__animated animate__slideInUp'
                            : ''
                        }
                      >
                        <Tab.Pane eventKey="first">
                          <Row>
                            {project.id === 1 ? (
                              <ProjectCard key={project.id} {...project} />
                            ) : null}
                          </Row>
                        </Tab.Pane>
                        <Tab.Pane eventKey="second">
                          <Row>
                            {project.id === 2 ? (
                              <ProjectCard key={project.id} {...project} />
                            ) : null}
                          </Row>
                        </Tab.Pane>
                        <Tab.Pane eventKey="third">
                          <Row>
                            {project.id === 3 ? (
                              <ProjectCard key={project.id} {...project} />
                            ) : null}
                          </Row>
                        </Tab.Pane>
                        <Tab.Pane eventKey="fourth">
                          <Row>
                            {project.id === 4 ? (
                              <ProjectCard key={project.id} {...project} />
                            ) : null}
                          </Row>
                        </Tab.Pane>
                      </Tab.Content>
                    ))}
                  </Tab.Container>
                </div>
              )}
            </TrackVisibility>
          </Col>
        </Row>
      </Container>
      <img className="background-image-right" src={colorSharp} alt=""></img>
    </section>
  );
};
