import { Col, NavItem } from 'react-bootstrap';
import Netflix from '../assets/img/netflix.png';
import Webshop from '../assets/img/webshop.png';
import firstWebsite from '../assets/img/website.png';

export const ProjectCard = ({ title, description, image, link }) => {
  const images = {
    netflix: Netflix,
    webshop: Webshop,
    firstWebsite: firstWebsite,
  };

  return (
    <Col size={12} sm={6} md={4}>
      <div className="proj-imgbx">
        <img src={images[image]} alt="" />
        <div className="proj-txtx">
          <a href={link}>
            <h4>{title}</h4>
          </a>
          <ul
            className="featureText"
            dangerouslySetInnerHTML={{ __html: description }}
          />{' '}
        </div>
      </div>
    </Col>
  );
};
