import { Col, Container, Row } from 'react-bootstrap';
import { ProjectCard } from './ProjectCard';
import colorSharp2 from '../assets/img/color-sharp.png';
import data from '../projectsDescriptions.json';
import 'animate.css';
import TrackVisibility from 'react-on-screen';

export const Projects = () => {
  return (
    <section className="project" id="project">
      <Container>
        <Row>
          <Col>
            <TrackVisibility>
              {({ isVisible }) => (
                <div
                  className={
                    isVisible ? 'animated__animated animate__bounce' : ''
                  }
                >
                  <h2>Projects</h2>
                  <p>
                    Please hover over project for more information. Also, by
                    clicking the title you will be redirected to the project
                    site itself.
                  </p>
                </div>
              )}
            </TrackVisibility>
            {/* <Tab.Container id="projects-tabs" defaultActiveKey="first"> */}
            {/* <Nav
                variant="pills"
                className="nav-pills mb-5 justify-content-center align-items-center"
                id="pills-tab"
              > */}
            {/* <Nav.Item>
                  <Nav.Link eventKey="first">Tab 1</Nav.Link>
                </Nav.Item>
                <Nav.Item>
                  <Nav.Link eventKey="second">Tab 2</Nav.Link>
                </Nav.Item>
                <Nav.Item>
                  <Nav.Link eventKey="third">Tab 3</Nav.Link>
                </Nav.Item> */}
            {/* </Nav> */}
            {/* <Tab.Content> */}
            {/* <Tab.Pane eventKey="first"> */}
            <Row>
              {data.map((project, index) => {
                return <ProjectCard key={index} {...project} />;
              })}
            </Row>
            {/* </Tab.Pane> */}
            {/* <Tab.Pane eventKey="second">Lorem ipsum</Tab.Pane>
                <Tab.Pane eventKey="third">lorem ipsum</Tab.Pane> */}
            {/* </Tab.Content> */}
            {/* </Tab.Container> */}
          </Col>
        </Row>
      </Container>
      <img className="background-image-right" src={colorSharp2} alt="" />
    </section>
  );
};
